mapper.update:
UPDATE item_img
SET file_name = #{fileName},
ord = #{ord},
rep_img = #{repImg}
WHERE uuid = #{uuid}

mapper.select:
SELECT i.item_id, i.category_id, i.item_sell_status, i.item_gubun, i.item_name, i.price, i.stock_number, i.item_detail, i.dc_rate, i.regdate, i.moddate, ii.uuid, ii.file_name, ii.ord, ii.rep_img FROM item i LEFT JOIN item_img ii ON i.item_id = ii.item_id WHERE i.category_id = #{categoryId}
AND i.item_name LIKE CONCAT('%', #{searchText}, '%')
ORDER BY i.item_id DESC LIMIT #{offset}, #{amount}

SELECT i.item_id, i.category_id, i.item_sell_status, i.item_gubun, i.item_name, i.price, i.stock_number, i.item_detail, i.dc_rate, i.regdate, i.moddate, ii.uuid, ii.file_name, ii.ord, ii.rep_img FROM item i LEFT JOIN item_img ii ON i.item_id = ii.item_id WHERE i.item_gubun = #{itemGubun}
AND i.item_name LIKE CONCAT('%', #{searchText}, '%')
ORDER BY i.item_id DESC LIMIT #{offset}, #{amount}
